/**
 * 言語コードからテキストを引く
 * - "ja-JP" なども "ja" に丸める
 */
export function getDebugTranscript(lang) {
  if (!lang) return DEBUG_TRANSCRIPTS.ja.trim();
  const normalized = String(lang).toLowerCase().split('-')[0]; // "ja-JP" -> "ja"
  if (DEBUG_TRANSCRIPTS[normalized]) {
    return DEBUG_TRANSCRIPTS[normalized].trim();
  }
  return DEBUG_TRANSCRIPTS.ja.trim();
}

// 将来的に一覧を見たい時用（必須ではない）
export function listDebugLocales() {
  return Object.keys(DEBUG_TRANSCRIPTS);
}

// デフォルト export しておいてもOK（使わなくても良い）
export default DEBUG_TRANSCRIPTS;
